//打印日志, 其中logLevel为日志级别: ERROR/WARN/DEBUG/INFO/TRACE
private void printLog(String message, Throwable ex, Level logLevel) {
  switch (logLevel) {
    case ERROR:
      logger.error(message, ex);
      break;
    case WARN:
      logger.warn(message, ex);
      break;
    case DEBUG:
      logger.debug(message, ex);
      break;
    case INFO:
      logger.info(message, ex);
      break;
    case TRACE:
      logger.trace(message, ex);
      break;
  }

  Tracer.logError(ex);
}

private boolean checkAccessToken(String token) {
  String accessTokens = bizConfig.getAdminServiceAccessTokens();

  // if user forget to configure access tokens, then default to pass
  if (Strings.isNullOrEmpty(accessTokens)) {
    return true;
  }

  // no need to check
  if (Strings.isNullOrEmpty(token)) {
    return false;
  }

  // update cache
  if (!accessTokens.equals(lastAccessTokens)) {
    synchronized (this) {
      accessTokenList = ACCESS_TOKEN_SPLITTER.splitToList(accessTokens);
      lastAccessTokens = accessTokens;
    }
  }

  return accessTokenList.contains(token);
}

@SuppressWarnings("unchecked")
public static <K, V> Map<K, V> mapByKey(String key, List<?> list) {
  Map<K, V> map = new HashMap<>();
  if (CollectionUtils.isEmpty(list)) {
    return map;
  }
  try {
    Class<?> clazz = list.get(0).getClass();
    Field field = deepFindField(clazz, key);
    if (field == null) {
        throw new IllegalArgumentException("Could not find the key");
    }
    field.setAccessible(true);
    for (Object o : list) {
      map.put((K) field.get(o), (V) o);
    }
  } catch (Exception e) {
    throw new BeanUtilsException(e);
  }
  return map;
}

private static String getWellFormName(String envName) {
  if (StringUtils.isBlank(envName)) {
    return "";
  }

  String envWellFormName = envName.trim().toUpperCase();

  // special case for production in case of typo
  if ("PROD".equals(envWellFormName)) {
    return Env.PRO.name;
  }

  // special case that FAT & FWS should map to FAT
  if ("FWS".equals(envWellFormName)) {
    return Env.FAT.name;
  }

  return envWellFormName;
}

@Transactional
public GrayReleaseRule updateRulesReleaseId(String appId, String clusterName,
                                 String namespaceName, String branchName,
                                 long latestReleaseId, String operator) {
  GrayReleaseRule oldRules = grayReleaseRuleRepository.
      findTopByAppIdAndClusterNameAndNamespaceNameAndBranchNameOrderByIdDesc(appId, clusterName, namespaceName, branchName);

  if (oldRules == null) {
    return null;
  }

  GrayReleaseRule newRules = new GrayReleaseRule();
  newRules.setBranchStatus(NamespaceBranchStatus.ACTIVE);
  newRules.setReleaseId(latestReleaseId);
  newRules.setRules(oldRules.getRules());
  newRules.setAppId(oldRules.getAppId());
  newRules.setClusterName(oldRules.getClusterName());
  newRules.setNamespaceName(oldRules.getNamespaceName());
  newRules.setBranchName(oldRules.getBranchName());
  newRules.setDataChangeCreatedBy(operator);
  newRules.setDataChangeLastModifiedBy(operator);

  grayReleaseRuleRepository.save(newRules);

  grayReleaseRuleRepository.delete(oldRules);

  return newRules;
}

@Override
public List<ServiceInstance> getInstances(String serviceName) {
  if (Objects.equals(serviceName, this.selfInstance.getServiceName())) {
    List<ServiceInstance> serviceInstances = this.delegate.getInstances(serviceName);
    if (containSelf(serviceInstances, this.selfInstance)) {
      // contains self instance already
      return serviceInstances;
    }

    // add self instance to result
    List<ServiceInstance> result = new ArrayList<>(serviceInstances.size() + 1);
    result.add(this.selfInstance);
    result.addAll(serviceInstances);
    return result;
  } else {
    return this.delegate.getInstances(serviceName);
  }
}
