static double slowCos(final double x, final double[] result) {

    final double[] xs = new double[2];
    final double[] ys = new double[2];
    final double[] facts = new double[2];
    final double[] as = new double[2];
    split(x, xs);
    ys[0] = ys[1] = 0.0;

    for (int i = FACT.length - 1; i >= 0; i--) {
        splitMult(xs, ys, as);
        ys[0] = as[0];
        ys[1] = as[1];

        if ((i & 1) != 0) { // skip odd entries
            continue;
        }

        split(FACT[i], as);
        splitReciprocal(as, facts);

        if ((i & 2) != 0) { // alternate terms are negative
            facts[0] = -facts[0];
            facts[1] = -facts[1];
        }

        splitAdd(ys, facts, as);
        ys[0] = as[0]; ys[1] = as[1];
    }

    if (result != null) {
        result[0] = ys[0];
        result[1] = ys[1];
    }

    return ys[0] + ys[1];
}

static void splitReciprocal(final double[] in, final double[] result) {
    final double b = 1.0 / 4194304.0;
    final double a = 1.0 - b;

    if (in[0] == 0.0) {
        in[0] = in[1];
        in[1] = 0.0;
    }

    result[0] = a / in[0];
    result[1] = (b * in[0] - a * in[1]) / (in[0] * in[0] + in[0] * in[1]);

    if (result[1] != result[1]) { // can happen if result[1] is NAN
        result[1] = 0.0;
    }

    /* Resplit */
    resplit(result);

    for (int i = 0; i < 2; i++) {
        /* this may be overkill, probably once is enough */
        double err = 1.0 - result[0] * in[0] - result[0] * in[1] -
            result[1] * in[0] - result[1] * in[1];
        /*err = 1.0 - err; */
        err *= result[0] + result[1];
        /*printf("err = %16e\n", err); */
        result[1] += err;
    }
}

private boolean containSameValues(double[] current,
                                  double[] expect) {
    if (expect.length != size) {
        throw new NeuralNetException(NeuralNetException.SIZE_MISMATCH,
                                     expect.length, size);
    }

    for (int i = 0; i < size; i++) {
        if (!Precision.equals(current[i], expect[i])) {
            return false;
        }
    }
    return true;
}

